version.script:
import org.efaps.admin.runlevel.RunLevel;
import org.efaps.db.Context;
import org.efaps.db.SearchQuery;
import org.efaps.esjp.admin.ui.UIUpdate;
import org.efaps.importer.DataImport;

import java.net.URL
import java.io.File;

EFAPS_LOGGER.info("Importing Data for ERP Basic Modul;");

RunLevel.init("shell");
RunLevel.execute();

SearchQuery query = new SearchQuery();
query.setQueryTypes("Admin_User_JAASKey");
query.addWhereExprEqValue("Key", "ERP_CurrencyAdmin");
query.addSelect("OID");
query.execute();
if (!query.next()) {
    URL url4 = new URL(EFAPS_ROOTURL, "Data/IMPORT_UserJAASKey.xml");
    DataImport imp4 = DataImport.readFile(url4);
    imp4.updateInDB();
}

UIUpdate update = new UIUpdate();


update.add2Menu("31854e2f-a6ab-4c91-aaf0-30cd112e8995", "f54e5a23-4760-4322-9600-756ee787cff5", 1);


update.add2Menu("9427d507-4793-4751-83e8-f08dbf2df55d", "8d9aae87-fa36-4b62-aba0-dbd9c0ca273a", 1);




update.add2Help("10c1efaf-af0a-4602-b6b5-8a5aff4ae38d", "dead549e-5cc6-49f9-9a79-8e33aa139f6d", -1);
